fn ex01_purist(input: &str) -> Option<i32> {
    input.chars().fold(Some(0i32), |acc, ch| acc.and_then(|v| match ch {
        '(' => Some(v + 1),
        ')' => Some(v - 1),
        _ => None,
    }))
}

fn ex01_readable(input: &str) -> Option<i32> {
    let mut counter = 0;
    for ch in input.chars() {
        if ch == '(' {
            counter += 1;
        } else if ch == ')' {
            counter -= 1;
        } else {
            return None;
        }
    }
    Some(counter)
}
