fn ex01_purist(input: &str) -> i32 {
    input.chars().fold(0, |acc, ch| acc + match ch {
        '(' => 1,
        ')' => -1,
        _ => panic!("Invalid input {ch}"),
    })
}

fn ex01_readable(input: &str) -> i32 {
    let mut counter = 0;
    for ch in input.chars() {
        counter += match ch {
            '(' => 1,
            ')' => -1,
            _ => panic!("Invalid input {ch}"),
        };
    }
    counter
}
